BEGIN TRANSACTION;
CREATE TABLE "tariff_skills" (
	`id`	int,
	`order`	real,
	`level`	text,
	`skill`	text,
	`shaped`	int,
	`shape_bonus`	real,
	`start_position`	text,
	`end_position`	text,
	`tariff`	real,
	`fig_notation`	text,
	`description`	text,
	`starred`	INTEGER DEFAULT 0,
	PRIMARY KEY(id)
);
COMMIT;
